{% block content %}
    <!-- page-wrapper Start-->
    <div class=\"page-wrapper compact-wrapper dark-sidebar\" id=\"pageWrapper\">
        <!-- Page Header Start-->
        {% include 'admin/admin_include/header.html.twig' %}
        <!-- Page Header Ends-->
        <!-- Page Body Start-->
        <div class=\"page-body-wrapper\">
            <!-- Page Sidebar Start-->
            {% include 'admin/admin_include/navigation.html.twig' %}
            <!-- Page Sidebar Ends-->
            <!-- index body start -->
            <div class=\"page-body\">
                <div class=\"title-header\">
                    <h5>Dodajte novu firmu</h5>
                </div>
                <!-- New User start -->
                <div class=\"container-fluid\">
                    <div class=\"row\">
                        <div class=\"col-12\">
                            <div class=\"row\">
                                <div class=\"col-sm-12\">
                                    <div class=\"card\">
                                        <div class=\"card-body\">
                                            <ul class=\"nav nav-pills mb-3\" id=\"pills-tab\" role=\"tablist\">
                                                <li class=\"nav-item\" role=\"presentation\">
                                                    <button class=\"nav-link active\" id=\"pills-home-tab\"
                                                            data-bs-toggle=\"pill\" data-bs-target=\"#pills-home\"
                                                            type=\"button\">Firma</button>
                                                </li>
                                            </ul>

                                            <div class=\"tab-content\" id=\"pills-tabContent\">
                                                <div class=\"tab-pane fade show active\" id=\"pills-home\" role=\"tabpanel\">
                                                    {% if form_errors(form.nazivFirme) or form_errors(form.pib) or form_errors(form.email) or form_errors(form.password.first) or form_errors(form.password.second) %}
                                                        <div class=\"alert alert-danger\" role=\"alert\">
                                                            {{ form_errors(form.nazivFirme) }}
                                                            {{ form_errors(form.pib) }}
                                                            {{ form_errors(form.email) }}
                                                            {{ form_errors(form.password.first) }}
                                                            {{ form_errors(form.password.second) }}
                                                        </div>
                                                    {% endif %}
                                                    {{ form_start(form, {'attr': {'class' : 'needs-validation'}})}}
                                                    <div class=\"card-header-1\">
                                                        <h5>Opšti podaci</h5>
                                                    </div>

                                                    <div class=\"row\">
                                                        <div class=\"mb-4 row align-items-center\">
                                                            <label class=\"form-label-title col-lg-2 col-md-3 mb-0\">PIB</label>
                                                            <div class=\"col-md-9 col-lg-10\">
                                                                {{ form_widget(form.pib) }}
                                                            </div>
                                                        </div>

                                                        <div class=\"mb-4 row align-items-center\">
                                                            <label class=\"form-label-title col-lg-2 col-md-3 mb-0\">Naziv firme</label>
                                                            <div class=\"col-md-9 col-lg-10\">
                                                                {{ form_widget(form.nazivFirme) }}
                                                            </div>
                                                        </div>

                                                        <div class=\"mb-4 row align-items-center\">
                                                            <label class=\"col-lg-2 col-md-3 col-form-label form-label-title\">Email</label>
                                                            <div class=\"col-md-9 col-lg-10\">
                                                                {{ form_widget(form.email) }}
                                                            </div>
                                                        </div>

                                                        <div class=\"mb-4 row align-items-center\">
                                                            <label class=\"col-lg-2 col-md-3 col-form-label form-label-title\">Lozinka</label>
                                                            <div class=\"col-md-9 col-lg-10\">
                                                                {{ form_widget(form.password.first) }}
                                                            </div>
                                                        </div>

                                                        <div class=\"row align-items-center\">
                                                            <label class=\"col-lg-2 col-md-3 col-form-label form-label-title\">Ponovite lozinku</label>
                                                            <div class=\"col-md-9 col-lg-10\">
                                                                {{ form_widget(form.password.second) }}
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <br>
                                                    <div class=\"button login\">
                                                        <button type=\"submit\" class=\"align-items-center btn btn-theme\">
                                                            <span>Dodajte </span>
                                                        </button>
                                                    </div>
                                                    {{ form_end(form) }}
                                                </div>

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <!-- All User Table Ends-->
                <!-- footer start-->
                {% include 'admin/admin_include/footer.html.twig' %}
                <!-- footer End-->
            </div>
            <!-- index body end -->
        </div>
        <!-- Page Body End -->
    </div>
    <!-- page-wrapper End-->

    <!-- Modal Start -->
    {% include 'admin/admin_include/logout.html.twig' %}
    <!-- Modal End -->
{% endblock content %}

{% block footerjavascript %}
    {#<script>#}
    {#    \$(document).ready(function () {#}
    {#        \$('#example').DataTable();#}
    {#    });#}
    {#</script>#}
{% endblock footerjavascript %}
", "admin_user/dodaj_firmu.html.twig", "/home/prikolic/public_html/prikolica/templates/admin_user/dodaj_firmu.html.twig");
    }
}
